changeSet.sql:
UPDATE Settings SET workDayBeginHour = (SELECT * FROM (SELECT calendar_workDayBeginHour FROM Settings WHERE id = "1")tmpTpl) WHERE id = "1";
UPDATE Settings SET workDayBeginHour = "8" WHERE id = "1" AND workDayBeginHour is NULL;
UPDATE Settings SET workDayEndHour = (SELECT * FROM (SELECT calendar_workDayEndHour FROM Settings WHERE id = "1")tmpTpl) WHERE id = "1";
UPDATE Settings SET workDayEndHour = "16" WHERE id = "1" AND workDayEndHour is NULL;
